mapper.select:
SELECT lcode, lname, l.scode as scode, sname, l.tcode as tcode, tname, l.bcode AS bcode, bname, lcontent, lprice, maxStudent, sdate, edate, stime, l.saveFile as saveFile, state, classroom
FROM subject s JOIN lecture l ON (s.scode=l.scode) JOIN teacher t ON (t.tcode=l.tcode) JOIN book b ON (l.bcode=b.bcode)
WHERE lcode = #{lcode}


SELECT l.lcode as lcode, sname, lname, sdate, edate, COUNT(r.lcode) AS 'regCnt', state FROM lecture l JOIN subject s ON (l.scode=s.scode) left OUTER JOIN register r ON (r.lcode=l.lcode)
WHERE sdate BETWEEN CURRENT_DATE AND DATE_ADD(CURRENT_DATE, INTERVAL +7 day) AND state IN ('off','close') GROUP BY r.lcode HAVING COUNT(*) < 5
    ORDER BY sdate limit #{postStart}, #{postCount}
